class 'Hellfire'

function Hellfire:__init()
	screen_size = Render.Size
	missile_mode = false
	missile_spawn_args = {}
	missile_spawn_args.model = "general.blz/wea51-a.lod"
	missile_spawn_args.collision = "areaset02.blz/go150_lod1-c_col.pfx" -- small chair
	missile_spawn_args.fixed = true
	--
	missile_speed = 75
	slow_angle = 145000
	--
	explosion_args = {}
	explosion_args.effect_id = 155
	explosion_args.angle = Angle(0, 0, 0)
	--
	missile_effect_args = {}
	missile_effect_args.effect_id = 246
	--
	ragdoll_effect_args = {}
	ragdoll_effect_args.effect_id = 189
	ragdoll_effect_args.angle = Angle(0, 0, 0)
	--
	max_time = 10000 -- in milliseconds
	current_target = nil
	locked = false
	can_lock = false
	--
	--gray_cover = ""
	gray_cover = ""
	cover = Image.Create(AssetLocation.Base64, gray_cover)
	cover:SetAlpha(.35)
	--
	sync_timer = Timer()
	--
	other_missiles = {}
	actors = {}
	-- 4 - missile sound + smoke
	-- 211 explosion
	-- 246 missile sound + smoke larger louder
	-- 252 explosion
	-- 344 lasers from helis?
	-- 358 missile sound + smoke
	-- 369 missile fuel empty death
	-- 377 explosion
end

function Hellfire:KeyDown(args)
	if args.key == string.byte("X") then
		if missile_mode == true then
			if missile_speed < 250 then
				missile_speed = missile_speed * 1.25
			end
		end
	elseif args.key == string.byte("V") then
		if missile_mode == true then
			Network:Send("MissileExplode", {pos = missile:GetPosition()})
			Events:Fire("DeleteFromInventory", {sub_item = "Hellfire", sub_amount = 1})
			Reset()
		end
	elseif args.key == string.byte("N") then
		if missile_mode == true and can_lock == true then
			locked = true
			can_lock = false
		end
	end
end

function Hellfire:ItemClick()
	if LocalPlayer:GetValue("CanHit") == false then
		Chat:Print("You cannot use a Hellfire in a safezone!", Color.Red)
		return
	end
	missile_mode = not missile_mode
	if missile_mode == true then
		fuel_timer = Timer()
		missile_spawn_args.position = LocalPlayer:GetPosition() + Vector3(0, 25, 0)
		missile_spawn_args.angle = Camera:GetAngle()
		missile = ClientStaticObject.Create(missile_spawn_args)
		missile_effect_args.position = Camera:GetPosition()
		missile_effect_args.angle = Camera:GetAngle()
		screen_size = Render.Size
		missile_effect = ClientEffect.Create(AssetLocation.Game, missile_effect_args)
		missile_event = Events:Subscribe("CalcView", hellfire, hellfire.AngleSet)
		missile_event2 = Events:Subscribe("PreTick", hellfire, hellfire.CalculatePath)
		missile_event3 = Events:Subscribe("Render", hellfire, hellfire.Render)
		missile_event4 = Events:Subscribe("InputPoll", hellfire, hellfire.InputPoll)
	else
		Reset()
	end
end

function Hellfire:AngleSet()
	if Vector3.Distance(missile:GetPosition(), LocalPlayer:GetPosition()) > 750 then
		Chat:Print("Missile out of range of base", Color(255, 255, 0))
		Network:Send("MissileExplode", {pos = missile:GetPosition()})
		Events:Fire("DeleteFromInventory", {sub_item = "Hellfire", sub_amount = 1})
		Reset()
	end
	--print(Vector3.Distance(missile:GetPosition(), LocalPlayer:GetPosition()))
	Camera:SetPosition(missile:GetPosition() + (missile:GetAngle() * Vector3.Forward * 5))
	if locked == false then
		missile:SetAngle(Camera:GetAngle())
	elseif locked == true then
		Camera:SetAngle(missile:GetAngle())
	end
	missile_effect:SetPosition(missile:GetPosition())
	missile_effect:SetAngle(Camera:GetAngle())
end

function Hellfire:CalculatePath()
	--Chat:Print(tostring(missile_speed), Color(0,255,0))
	local ray = Physics:Raycast(Camera:GetPosition(), Camera:GetAngle() * Vector3.Forward, 0, 505)
	if missile_speed > 150 then
		if ray.distance < 4.5 then
			local campos = Camera:GetPosition()
			for static in Client:GetStaticObjects() do
				if static:GetValue("IsClaimOBJ") then
					local dist = Vector3.Distance(campos, static:GetPosition())
					if dist < 7.5 then
						local steam_id = tostring(static:GetValue("SteamIDid"))
						if not LocalPlayer:GetValue("Friends"):find(steam_id) and steam_id ~= tostring(LocalPlayer:GetSteamId().id) then
							Events:Fire("LC_ShootClaimObject", {target = static, damage = (7.5 - dist) * 11, absolute_damage = true})
						end
					end
				end
			end
			missile_mode = false
			Network:Send("MissileExplode", {pos = Camera:GetPosition()})
			Events:Fire("DeleteFromInventory", {sub_item = "Hellfire", sub_amount = 1})
			Reset()
		end
	else
		if ray.distance < 3.5 then	
			local campos = Camera:GetPosition()
			for static in Client:GetStaticObjects() do
				if static:GetValue("IsClaimOBJ") then
					local dist = Vector3.Distance(campos, static:GetPosition())
					if dist < 7.5 then
						local steam_id = tostring(static:GetValue("SteamIDid"))
						if not LocalPlayer:GetValue("Friends"):find(steam_id) and steam_id ~= tostring(LocalPlayer:GetSteamId().id) then
							Events:Fire("LC_ShootClaimObject", {target = static, damage = (7.5 - dist) * 11, absolute_damage = true})
						end
					end
				end
			end
			missile_mode = false
			Network:Send("MissileExplode", {pos = Camera:GetPosition()})
			Events:Fire("DeleteFromInventory", {sub_item = "Hellfire", sub_amount = 1})
			Reset()
		end
	end
	
	if not IsValid(current_target) then
		current_target = nil
	end
	
	if locked == false and ray.entity then
		local e_type = ray.entity.__type
		if e_type == "Player" or e_type == "Vehicle" then
			if not current_target then
				current_target = ray.entity
				can_lock = true
			elseif current_target.__type ~= ray.entity.__type then
				current_target = ray.entity
				can_lock = true
			elseif current_target ~= ray.entity then
				current_target = ray.entity
				can_lock = true
			end
		end
	end
	
	if locked == false then
		missile:SetPosition(Camera:GetPosition() + (Camera:GetAngle() * (Vector3.Forward * (-5 + (missile_speed / 50)))))
		slow_angle = slow_angle + 100
		Mouse:SetPosition(Vector2(screen_size.x * .5, screen_size.y * .5))
	elseif locked == true and IsValid(current_target) then
		local target_pos = current_target:GetPosition()
		local bullet_pos = missile:GetPosition()
		local angleyaw = Angle.FromVectors(Vector3.Forward, target_pos - bullet_pos).yaw
		local anglepitch = Angle.FromVectors(Vector3.Forward, target_pos - bullet_pos).pitch
		local ang = Angle(angleyaw, anglepitch, 0)
		missile:SetAngle(ang)
		missile:SetPosition(Camera:GetPosition() + (missile:GetAngle() * (Vector3.Forward * (-5 + (missile_speed / 50)))))
	end
end

function Hellfire:Render()
	cover:Draw(Vector2(0, 0), screen_size, Vector2(0, 0), Vector2(1, 1))
	local transform = Transform3()
	for ply in Client:GetStreamedPlayers() do
		transform:Translate(ply:GetPosition())
		transform:Rotate(Angle(0, 0.5 * math.pi, 0))
		Render:SetTransform(transform)
		Render:FillCircle(Vector3.Zero, 1.25, Color(255, 0, 0, 150))
		Render:ResetTransform()
	end
	for wno_id, bool in pairs(actors) do
		local WNO = WorldNetworkObject.GetById(wno_id)
		if IsValid(WNO) then
			if WNO:GetValue("Alive") == true then
				local transform = Transform3()
				transform:Translate(WNO:GetPosition())
				transform:Rotate(Angle(0, 0.5 * math.pi, 0))
				Render:SetTransform(transform)
				Render:FillCircle(Vector3.Zero, 1.25, Color(255, 255, 0, 150))
				Render:ResetTransform()
			end
		end
	end
	if max_time - fuel_timer:GetMilliseconds() > 0 then
		Render:FillArea(Vector2(0, screen_size.y * .96), Vector2(screen_size.x * ((max_time - fuel_timer:GetMilliseconds()) / max_time), screen_size.y), Color(0, 255, 0, 100))
	else
		Network:Send("MissileExplode", {pos = Camera:GetPosition()})
		Events:Fire("DeleteFromInventory", {sub_item = "Hellfire", sub_amount = 1})
		Reset()
	end
	
	if can_lock == true and IsValid(current_target) then
		if current_target.__type == "Player" then
			local name = tostring(current_target:GetName())
			local width = Render:GetTextWidth("Press ' N ' to lock on to " .. name, TextSize.Default * 2)
			local height = Render:GetTextHeight("Press ' N ' to lock on to " .. name, TextSize.Default * 2)
			Render:DrawText(Vector2((screen_size.x * .5) - (width / 2), screen_size.y * .96 - (height * 1.2)), "Press ' N ' to lock on to " .. name, Color(255, 255, 0), TextSize.Default * 2)
		elseif current_target.__type == "Vehicle" then
			local driver = tostring(current_target:GetDriver())
			local name
			if driver then
				name = driver .. "'s " .. tostring(current_target:GetName())			
			else
				name = tostring(current_target:GetName())
			end
			local width = Render:GetTextWidth("Press ' N ' to lock on to " .. name, TextSize.Default * 2)
			local height = Render:GetTextHeight("Press ' N ' to lock on to " .. name, TextSize.Default * 2)
			Render:DrawText(Vector2((screen_size.x * .5) - (width / 2), screen_size.y * .96 - (height * 1.2)), "Press ' N ' to lock on to " .. name, Color(255, 255, 0), TextSize.Default * 2)
		end
	end
	--Render:DrawLine(Vector2(screen_size.x * .48, screen_size.y * 48), Vector2(screen_size.x * .52, screen_size.y * .48), Color(255, 0, 0)) -- bottom across
	--Render:DrawLine(Vector2(screen_size.x * .48, screen_size.y * 48), Vector2(screen_size.x * .52, screen_size.y * .48), Color(255, 0, 0)) -- bottom across
end

function Hellfire:CreateExplosion(args) -- receives pos
	explosion_args.position = args.pos
	local hellfire_explosion = ClientEffect.Create(AssetLocation.Game, explosion_args)
	if Vector3.Distance(args.pos, LocalPlayer:GetPosition()) < 35 then
		ragdoll_effect_args.position = LocalPlayer:GetPosition()
		local ragdoll_effect = ClientEffect.Create(AssetLocation.Game, ragdoll_effect_args)
	end
	Events:Fire("HellfireExplosion", {pos = args.pos})
end

function Hellfire:VehicleRender() -- permanent render
	if missile_mode == true then
		if sync_timer:GetSeconds() > .75 then -- config sync frequency here
			local player_table = {}
			for ply in Client:GetStreamedPlayers() do
				table.insert(player_table, ply)
			end
			Network:Send("SendUpdate", {players = player_table, pos = missile:GetPosition(), ang = missile:GetAngle(), speed = missile_speed})
			sync_timer:Restart()
		end
		width = Render:GetTextWidth("Press ' X ' to accelerate", TextSize.Default * 1.2)
		Render:DrawText(Vector2(screen_size.x - width, screen_size.y * .1), "Press ' X ' to accelerate", Color(255, 255, 0), TextSize.Default * 1.2)
		width = Render:GetTextWidth("Press ' V ' to manually detonate", TextSize.Default * 1.2)
		Render:DrawText(Vector2(screen_size.x - width, screen_size.y * .15), "Press ' V ' to manually detonate", Color(255, 255, 0), TextSize.Default * 1.2)
	end
end

function Hellfire:OtherMissileData(args) -- receives pos, ang, speed, player(id)
	if not other_missiles[args.id] then
		other_missiles[args.id] = {}
		missile_spawn_args.position = args.pos
		missile_spawn_args.angle = args.ang
		missile_effect_args.position = args.pos
		missile_effect_args.angle = args.ang
		other_missiles[args.id].missile = ClientStaticObject.Create(missile_spawn_args)
		other_missiles[args.id].effect = ClientEffect.Create(AssetLocation.Game, missile_effect_args)
		other_missiles[args.id].time = os.clock()
	end
	other_missiles[args.id].pos = args.pos
	other_missiles[args.id].ang = args.ang
	other_missiles[args.id].speed = args.speed
	local missile_ex = other_missiles[args.id].missile
	missile_ex:SetPosition(args.pos)
	missile_ex:SetAngle(args.ang)
end

function Hellfire:SimulateOtherMissiles()
	for ply_id, itable in pairs(other_missiles) do
		local external_missile = other_missiles[ply_id].missile
		local external_effect = other_missiles[ply_id].effect
		if IsValid(external_missile) == false or os.clock() - other_missiles[ply_id].time > max_time / 1000 then
			--Chat:Print("Missile Removed", Color(255, 255, 0))
			other_missiles[ply_id] = nil
			external_missile:Remove()
			external_effect:Remove()
		end
		external_missile:SetPosition(external_missile:GetPosition() + (itable.ang * (Vector3.Forward * ((-5 +itable.speed) / 50))))
		external_effect:SetPosition(external_missile:GetPosition() + (external_missile:GetAngle() * (Vector3.Backward * 1.3)))
		external_effect:SetAngle(external_missile:GetAngle())
	end
end

function Hellfire:InputPoll(args)
	Input:SetValue(Action.LookUp, Input:GetValue(Action.LookUp) / slow_angle)
	Input:SetValue(Action.LookDown, Input:GetValue(Action.LookDown) / slow_angle)
	Input:SetValue(Action.LookLeft, Input:GetValue(Action.LookLeft) / slow_angle)
	Input:SetValue(Action.LookRight, Input:GetValue(Action.LookRight) / slow_angle)
end

function Hellfire:WNOCreate(args)
	if args.object:GetValue("M_ID") then
		local id = args.object:GetId()
		if not actors[id] then
			actors[id] = true
		end
	end
end

function Hellfire:WNODestroy(args)
	if args.object:GetValue("M_ID") then
		local id = args.object:GetId()
		if actors[id] then
			actors[id] = nil
		end
	end
end

function Hellfire:OnUnload()
	if IsValid(missile) then missile:Remove() end
	if IsValid(missile_effect) then missile_effect:Remove() end
end

function Reset()
	if IsValid(missile) then missile:Remove() end
	if IsValid(missile_effect) then missile_effect:Remove() end
	missile_mode = false
	fuel_timer = nil
	Events:Unsubscribe(missile_event)
	Events:Unsubscribe(missile_event2)
	Events:Unsubscribe(missile_event3)
	Events:Unsubscribe(missile_event4)
	missile_speed = 75
	slow_angle = 200000
	locked = false
	can_lock = false
end

hellfire = Hellfire()

Events:Subscribe("KeyDown", hellfire, hellfire.KeyDown)
Events:Subscribe("ModuleUnload", hellfire, hellfire.OnUnload)
Events:Subscribe("Render", hellfire, hellfire.VehicleRender)
Events:Subscribe("PostTick", hellfire, hellfire.SimulateOtherMissiles)
Events:Subscribe("WorldNetworkObjectCreate", hellfire, hellfire.WNOCreate)
Events:Subscribe("WorldNetworkObjectDestroy", hellfire, hellfire.WNODestroy)
--
Events:Subscribe("UseHellfire", hellfire, hellfire.ItemClick)
--
Network:Subscribe("CreateExplosion", hellfire, hellfire.CreateExplosion)
Network:Subscribe("OtherMissileData", hellfire, hellfire.OtherMissileData)